var status={
    "key":"",
    "EXPIRES":20*60*1000,
    "switch":0,
    "init":init,
    "judge":judge
}

function init() {
    this.key=(new Date).getTime();
    this.switch=1;
}

function judge() {
    var now=(new Date).getTime();
    if(now-this.key>this.EXPIRES){
        this.switch=0;
    }else{
        this.key=now;
    }
}


exports.status=status;